// src/components/QuizComponent.jsx
import React, { useState } from 'react';
import { useQuiz } from '../hooks/useQuiz';

export default function QuizComponent({ onComplete }) {
  const { questions, answers, timeLeft, handleAnswer, submitQuiz } = useQuiz();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [quizComplete, setQuizComplete] = useState(false);

  const handleSubmit = async () => {
    try {
      const result = await submitQuiz();
      if (result.score >= 70) {
        onComplete();
      }
      setQuizComplete(true);
    } catch (error) {
      console.error('Error submitting quiz:', error);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-4">
      {!quizComplete ? (
        <div className="bg-white rounded-lg shadow p-6">
          <div className="text-right mb-4">
            Time Left: {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, '0')}
          </div>

          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-4">
              Question {currentIndex + 1} of {questions.length}
            </h2>
            <p className="text-gray-700">{questions[currentIndex]?.question}</p>
          </div>

          <div className="space-y-3">
            {questions[currentIndex]?.options.map((option, idx) => (
              <button
                key={idx}
                onClick={() => handleAnswer(questions[currentIndex].id, option)}
                className={`w-full p-3 text-left rounded-lg border ${
                  answers[questions[currentIndex].id] === option
                    ? 'bg-blue-100 border-blue-500'
                    : 'hover:bg-gray-50'
                }`}
              >
                {option}
              </button>
            ))}
          </div>

          <div className="mt-6 flex justify-between">
            <button
              onClick={() => setCurrentIndex(prev => prev - 1)}
              disabled={currentIndex === 0}
              className="px-4 py-2 bg-gray-200 rounded disabled:opacity-50"
            >
              Previous
            </button>
            {currentIndex === questions.length - 1 ? (
              <button
                onClick={handleSubmit}
                className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
              >
                Submit Quiz
              </button>
            ) : (
              <button
                onClick={() => setCurrentIndex(prev => prev + 1)}
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Next
              </button>
            )}
          </div>
        </div>
      ) : (
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Quiz Complete!</h2>
          <p className="text-lg mb-4">Your score is being calculated...</p>
        </div>
      )}
    </div>
  );
}
