// src/App.js
import React, { useState } from 'react';
import { HomePage, QuizComponent, AuthComponent, CertificateView } from './components';
import { PAGES } from './constants';

export default function App() {
  const [currentPage, setCurrentPage] = useState(PAGES.HOME);
  const [userData, setUserData] = useState(null);

  const handleAuth = (user) => {
    setUserData(user);
    setCurrentPage(PAGES.HOME);
  };

  const handleQuizComplete = () => {
    setCurrentPage(PAGES.CERTIFICATE);
  };

  const navigateToPage = (page) => {
    setCurrentPage(page);
  };

  const renderPage = () => {
    switch(currentPage) {
      case PAGES.QUIZ:
        return <QuizComponent onComplete={handleQuizComplete} />;
      case PAGES.LOGIN:
      case PAGES.REGISTER:
        return <AuthComponent type={currentPage} onAuth={handleAuth} />;
      case PAGES.CERTIFICATE:
        return <CertificateView onBack={() => navigateToPage(PAGES.HOME)} />;
      default:
        return (
          <HomePage 
            onStart={() => navigateToPage(userData ? PAGES.QUIZ : PAGES.REGISTER)}
            onLogin={() => navigateToPage(PAGES.LOGIN)}
            onRegister={() => navigateToPage(PAGES.REGISTER)}
          />
        );
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {renderPage()}
    </div>
  );
}
